<template>
    <li :class="'msg-' + this.msg.type">
      <span class="content">
        <!-- <t2sclass="speaker" :text="msg.content">X</t2s> -->
          <span v-if="msg.type==='bot'"
                v-bind:class="['t2s', isReading ? 'btn-reading' : 'btn-read']"
                v-bind:title="!isReading ? 'Lire texte' : 'Arreter la lecture'"
                @click="!isReading ? speak() : stop()">
          </span>
        {{this.msg.content.trim()}}
      </span>
    </li>

</template>

<script>

export default {
  name: "chatMsg",
  props: ["msg"],
  data() {
    return {
      isReading: false
    };
  },
  created(){
    this.$bus.$on('t2s-speaking-'+this.msg.time, _ => this.isReading = true);
    this.$bus.$on('t2s-done-'+this.msg.time, _ => this.isReading = false);
  },
  computed: {
    cssClass() {
      return "msg-" + this.msg.type;
    }
  },
  methods: {
    speak() {
      if (!this.isReading) {
        this.$bus.$emit('t2s-speak',this.msg.content,this.msg.time);
      }
    },

    stop() {
      this.$bus.$emit('t2s-cancel',this.msg.time);
    }
  }
};
</script>

